import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { qnBuffer, choiceBuffer } from "./Home";
import {EditButton, StyledButton, StyledDiv, StyledForm, StyledFormWrapper, StyledHeader} from "./Style.jsx"

function ResponseDisplay() {

  const navigate = useNavigate()
  const resp_url = "/questions/responses"
  const [list, setList] = useState([])
  const [resp, setResp] = useState({
    name: " ",
    colour: {
      Blue: false,
      Red: false,
      Green: false,
      Yellow: false,
      Purple: false,
      Pink: false
    },
    prog_lang: {
      Python: false,
      Java: false,
      Ruby: false,
      JavaScript: false,
      Golang: false
    },
    languages: {
      English: false,
      Chinese: false,
      Malay: false,
      Tamil: false,
      Hindi: false
    },
    would_pay: {
      Yes: false,
      No: false
    }
  })

  async function handleEdit(index) {
    const response = await fetch(resp_url, newResponse) // post request to server 
    const server_response = await response.json()
    console.log("server replied: ", server_response)
    navigate(`/Summary/${index}`) // navigates to summary page 
  }

  // fetch data
  useEffect(() => {
    fetch("http://localhost:3500/questions/responses")
      .then(res => res.json())
      .then(resJson => {
        setList(resJson.data)
        console.log("fetched: ", resJson.data)
      })
  }, [])

  function displayBox(item) {
    return (
      <label>
        <p>name: {item.name}</p>
        <p>would_pay: {JSON.stringify(item.would_pay)}</p>
      </label>
    )
  }

  function displayResult(items=[]) {
    items.forEach((item, index) => {
      responses.push(<StyledDiv>
        <EditButton id={index} onClick={handleEdit(index)}>Edit</EditButton>
        {displayBox(item)}
        </StyledDiv>)
      console.log("displaying: ", index)
    })
  }

  let responses = []
  displayResult(list)

  return (
    <div>
      <button onClick={() => navigate(-1)}>Back</button>
      <div>{responses}</div>
    </div>
  )
}

export default ResponseDisplay
