import { useState, useEffect } from 'react';

function Survey() {
  // user's response
  const [name, setName] = useState("");
  const [colour, setColour] = useState("");

  // backend data 
  const [qn, setQn] = useState({})
  const [qns, setQns] = useState([])
  // const [list, setList] = useState(true)
  // const [card, setCard] = useState(true)

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(res => setQns(res))
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(qns.data[0].question)
    alert(`Response submitted! Have a good day!`)
  }
//{qns.data[1].question}
  return (
      <form onSubmit={handleSubmit}>
        <label>
          <p>{qns.data[0].question}</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>

        <label>
          <p>{qns.data[1].question}</p>
          <input 
            type="text"
            value={colour}
            onChange={(e) => setColour(e.target.value)}
          />


        </label>

        <label>
          <p>{qns.data[2].question}</p>
        </label>

        <label>
          <p>{qns.data[3].question}</p>
        </label>

        <label>
          <p>{qns.data[4].question}</p>
        </label>

        <input type="submit" />
      </form>
  )
}

export default Survey;


