import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { qnBuffer, choiceBuffer } from "./Home";

function Summary() {

  const userId = parseInt(useParams().id)
  const navigate = useNavigate()
  const [resp, setResp] = useState(0)
  const handleEdit = () => {
    navigate(-1)
  }

  useEffect(async () => {
    await fetch(`http://localhost:3500/questions/responses/${userId}`)
      .then(res => res.json())
      .then(resJson => {setResp(resJson.data)})
  }, [])

  function displaySelected(items) {
    const selected = []
    const curr = 0
    for (var key in items) {
      if(items[key]) { // if selected
        selected[curr] = items[key]
        curr++
      } else {
        // continue
      }
    }

  }

  return (
    <div>
      <h3>Your response has been submitted</h3>
      <div>
      <h5>Summary</h5>
      <div>{"1) " + qnBuffer[0]}
      <p>{resp.name}</p></div>

      <div>{"2) " + qnBuffer[1]}
      <p>{resp.colour}</p></div>

      <div>{"3) " + qnBuffer[2]}
      <p>{resp.prog_lang}</p></div>

      <div>{"4) " + qnBuffer[3]}
      <p>{resp.langugaes}</p></div>

      <div>{"5) " + qnBuffer[4]}
      <p>{resp.would_pay}</p></div>
      </div>
      <button onClick={handleEdit}>Edit</button>
    </div>
  );
};

export default Summary;
