import React from 'react'
import { useState, useEffect } from 'react'
import { Route, BrowserRouter as Router, Routes, Redirect } from "react-router-dom";
import ReactDOM from 'react-dom'

import Home from "./components/Home"
import Summary from "./components/Summary"

function Survey() {
  // user's response
  const [name, setName] = useState("");
  const [colour, setColour] = useState("");

  // backend data 
  const [list, setList] = useState([]);

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(resJson => setList(resJson.data))
  }, [])

  let qnBuffer = [""]
  let choiceBuffer = [[], []]

  async function buffer() {
    var i = 0;
    try {
      await list.map(qn => {
        qnBuffer[i] = qn.question
        choiceBuffer[i] = cloneArr(qn.choices)
        i++;
      })
    } catch (error) {
      console.log("ERROR! List not ready")
    }
  }
  buffer()

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(name)
    console.log(colour)

    alert(`Response submitted! Have a good day!`)
  }

  function getOption(_id) {
    var opt = document.getElementById(_id)
    console.log(opt.value)
    return (opt.value)
  }

  return (
    <Router>
      <div>
        <form onSubmit={handleSubmit}>

          <label>
            <p>{qnBuffer[0]}</p>
            <input 
              type="text"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
          </label>

          <label>
          <p>{qnBuffer[1]}</p>
          <select id="qn2" onChange={() => setColour(getOption("qn2"))}>
            {generateDropdown(choiceBuffer[1])}
          </select>
          </label>

          <p><input type="submit" /></p>
        </form>
      </div>
      <Routes>
        <Route path="/" element={Hom}/>
      </Routes>
    </Router>
  )
}

function cloneArr(arr) {
  let temp = []
  var i = 0;
  arr.map(item => {
    temp[i] = item
    i++
  })
  return (temp)
}

/**
 * Returns a HTML script to generate dropdowns
 * 
 * @param {*} arr 
 * @returns 
 */
function generateDropdown(arr) {
  return (
    arr.map(opt => <option>{opt}</option>)
  )
}

export default Survey;
