import React from 'react'
import { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect,} from "react-router-dom";
import ReactDOM from 'react-dom'
//import Summary from "./components/Summary"

function Survey() {
  // user's response
  const [name, setName] = useState("");

  // backend data 
  //const [qn, setQn] = useState({})
  const [qns, setQns] = useState([])
  // const [list, setList] = useState(true)
  // const [card, setCard] = useState(true)

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(res => {
      let temp = res
      setQns(temp)
    })
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    // console.log(qns)
    // console.log(qns.data)
    // console.log(qns.data[0])
    // console.log(qns.data[0].question)
    qns.data.forEach(console.log)
    //alert(`Response submitted! Have a good day!`)
  }

  const colors = ['red','blue','green','purple']
  return (
    <div>
      {qns.data.map(qn => {
        return (
          <div>printing...</div>
        )
      })}
      <form onSubmit={handleSubmit}>
        <label>
          <p>name?</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <input type="submit" />
      </form>
    </div>
  )
}

