import React from "react";
import { useState } from 'react';
import {qnBuffer, choiceBuffer} from "./Home";
import { useNavigate, Link } from "react-router-dom";
import {StyledButton, StyledForm, StyledFormWrapper} from "./Style.js"

function PostForm() {
  const resp_url = "/questions/responses" // to server 
  const navigate = useNavigate()
  const [data, setData] = useState({
    name: "",
    colour: {
      Blue: false,
      Red: false,
      Green: false,
      Yellow: false,
      Purple: false,
      Pink: false
    },
    prog_lang: {
      Python: false,
      Java: false,
      Ruby: false,
      JavaScript: false,
      Golang: false
    },
    languages: {
      English: false,
      Chinese: false,
      Malay: false,
      Tamil: false,
      Hindi: false
    },
    would_pay: {
      Yes: false,
      No: false
    }
  })

  function handleChange(e) {
    const newData = { ...data } // copy curr data
    newData[e.target.id] = e.target.value
    setData(newData)
  }

  function handleCheckbox(e) {
    const newData = { ...data } // copy curr data
    const updatedField = newData[e.target.id]
    updatedField[e.target.value] = e.target.checked
    newData[e.target.id] = updatedField
    setData(newData)
  }

  async function handleSubmit(e) {
    e.preventDefault();
    const newResponse = {
      method: "POST",
      mode: "cors",
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(data)
    }
    const response = await fetch(resp_url, newResponse) // post request to server 
    const server_response = await response.json()
    console.log("server replied:", server_response, server_response.id)
    const userId = server_response.id
    navigate(`/Summary/${userId}`)
  }

  function generateCheckBoxes_colour(arr =[], _id, _type) {
    return (
      arr.map(item => 
      <p><input type={_type} id={_id} value={item} onChange={handleCheckbox} checked={data.colour.item}/> {item}</p>
      )
    )
  }

  function generateCheckBoxes_prog(arr =[], _id, _type) {
    return (
      arr.map(item => 
      <p><input type={_type} id={_id} value={item} onChange={handleCheckbox} checked={data.prog_lang.item}/> {item}</p>
      )
    )
  }

  function generateCheckBoxes_lang(arr =[], _id, _type) {
    return (
      arr.map(item => 
      <p><input type={_type} id={_id} value={item} onChange={handleCheckbox} checked={data.languages.item}/> {item}</p>
      )
    )
  }

  function generateCheckBoxes_payment(arr =[], _id, _type) {
    return (
      arr.map(item => 
      <p><input type={_type} id={_id} value={item} onChange={handleCheckbox} checked={data.would_pay.item}/> {item}</p>
      )
    )
  }

  return (
    <div>
      <StyledButton onClick={() => navigate(-1)}>Back</StyledButton>
      <StyledFormWrapper>
      <StyledForm onSubmit={e => handleSubmit(e)}>
        <div>
          <label>{qnBuffer[0]}</label>
          <p><input type="text" required id="name" value={data.name} onChange={handleChange} /></p>
        </div>
        <div>
          <label>{qnBuffer[1]}</label>
          <p>{generateCheckBoxes_colour(choiceBuffer[1], data.colour, "checkbox")}</p>

        </div>
        <div>
          <label>{qnBuffer[2]}</label>
          <p>{generateCheckBoxes_prog(choiceBuffer[2], data.prog_lang, "radio")}</p>
        </div>
        <div>
          <label>{qnBuffer[3]}</label>
          <p>{generateCheckBoxes_lang(choiceBuffer[3], data.languages, "checkbox")}</p>
        </div>
        <div>
          <label>{qnBuffer[4]}</label>
          <p>{generateCheckBoxes_payment(choiceBuffer[4], data.would_pay, "radio")}</p>
        </div>
        <StyledButton type="submit">submit</StyledButton>
      </StyledForm>
      </StyledFormWrapper>
    </div>
  )
}

export default PostForm
