import React, { useState, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { qnBuffer, choiceBuffer } from "./Home";

function Summary() {

  const userId = parseInt(useParams().id)
  const navigate = useNavigate()
  const [resp, setResp] = useState(0)

  const handleEdit = () => {


    navigate(-1)
  }

  useEffect(async () => {
    await fetch(`http://localhost:3500/questions/responses/${userId}`)
      .then(res => res.json())
      .then(resJson => {setResp(resJson.data)})
  }, [])

  function displaySelected(items) {
    const selected = []
    var curr = 0
    for (var key in items) {
      if(items[key]) { // if selected
        selected[curr] = key
        curr++
      } else {
        // continue
      }
    }
    return (
      selected.map(item => <div>{item}</div>)
    )
  }

  function handleChange(e) {
    const newData = { ...resp } // copy curr data
    newData[e.target.id] = e.target.value
    setData(newData)
  }

  function handleCheckbox(e) {
    const newData = { ...resp } // copy curr data
    const updatedField = newData[e.target.id]
    updatedField[e.target.value] = e.target.checked
    newData[e.target.id] = updatedField
    setData(newData)
  }

  return (
  //   <div>
  //     <h3>Your response has been submitted</h3>
  //     <div>
  //     <h5>Summary</h5>
  //     <div>{"1) " + qnBuffer[0]}
  //     <p>{resp.name}</p></div>

  //     <div>{"2) " + qnBuffer[1]}</div>
  //     <div>{displaySelected(resp.colour)}</div>

  //     <div>{"3) " + qnBuffer[2]}</div>
  //     <div>{resp.prog_lang}</div>

  //     <div>{"4) " + qnBuffer[3]}</div>
  //     <div>{displaySelected(resp.languages)}</div>

  //     <div>{"5) " + qnBuffer[4]}</div>
  //     <div>{resp.would_pay}</div>
  //     </div>
  //     <button onClick={handleEdit}>Edit</button>
  //   </div>
  // );

  <div>
      <form onSubmit={e => handleEdit(e)}>
        <div>
          <label>{qnBuffer[0]}</label>
          <p><input type="text" id="name" value={resp.name} onChange={handleChange} /></p>
        </div>
        <div>
          <label>{qnBuffer[1]}</label>
          <p><input type="checkbox" id="colour" value="Blue" onChange={handleCheckbox} checked={resp.colour.Blue}/> Blue</p>
          <p><input type="checkbox" id="colour" value="Red" onChange={handleCheckbox} checked={resp.colour.Red}/> Red</p>
          <p><input type="checkbox" id="colour" value="Green" onChange={handleCheckbox} checked={resp.colour.Green}/> Green</p>
          <p><input type="checkbox" id="colour" value="Yellow" onChange={handleCheckbox} checked={resp.colour.Yellow}/> Yellow</p>
          <p><input type="checkbox" id="colour" value="Purple" onChange={handleCheckbox} checked={resp.colour.Purple}/> Purple</p>
          <p><input type="checkbox" id="colour" value="Pink" onChange={handleCheckbox} checked={resp.colour.Pink}/> Pink</p>
        </div>
        <div>
          <label>{qnBuffer[2]}</label>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Python" onChange={handleChange}/> Python</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Java" onChange={handleChange}/> Java</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Ruby" onChange={handleChange}/> Ruby</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="JavaScript" onChange={handleChange}/> JavaScript</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Golang" onChange={handleChange}/> Golang</p>
        </div>
        <div>
          <label>{qnBuffer[3]}</label>
          <p><input type="checkbox" id="languages" value="English" onChange={handleCheckbox} checked={resp.languages.English}/> English</p>
          <p><input type="checkbox" id="languages" value="Chinese" onChange={handleCheckbox} checked={resp.languages.Chinese}/> Chinese</p>
          <p><input type="checkbox" id="languages" value="Malay" onChange={handleCheckbox} checked={resp.languages.Malay}/> Malay</p>
          <p><input type="checkbox" id="languages" value="Tamil" onChange={handleCheckbox} checked={resp.languages.Tamil}/> Tamil</p>
          <p><input type="checkbox" id="languages" value="Hindi" onChange={handleCheckbox} checked={resp.languages.Hindi}/> Hindi</p>
        </div>
        <div>
          <label>{qnBuffer[4]}</label>
          <p><input type="radio" name="would_pay" id="would_pay" value="Yes" onChange={handleChange}/> Yes</p>
          <p><input type="radio" name="would_pay" id="would_pay" value="No" onChange={handleChange}/> No</p>
        </div>
        <button type="submit">Edit</button>
      </form>
    </div>
  )
};

export default Summary;
