import React from 'react'
import { useState, useEffect } from 'react'
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';
import FormDataComponent from './form-data-component';

//const [list, setList] = useState([])

function Home() {

  const [list, setList] = useState([{}])
  var qnBuffer = []
  var choiceBuffer = []

  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
      .then(res => res.json())
      .then(resJson => {
        console.log(resJson)
        setList(resJson)
        console.log(list)
      })
  }, [])

  function cloneArr(arr = []) {
    let len = arr.length
    let temp = []
    if(len != 0) {
      var i = 0;
      arr.map(item => {
        temp[i] = item
        i++
      })
    }
    return temp
  }

  async function buffer() {
    var i = 0;
    try {
      await this.list.map(qn => {
        this.qnBuffer[i] = qn.question
        this.choiceBuffer[i] = cloneArr(qn.choices)
        i++;
      })
    } catch (error) {
      console.log("ERROR! List not ready")
      console.log(error)
    }
  }
  buffer()

  return (
    <div>
      <FormDataComponent />
    </div>
  )
}

export default Home;
//export {list};
