import React, { Component } from 'react';
import { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom";
import '../../node_modules/bootstrap/dist/css/bootstrap.min.css';

export default class FormDataComponent extends Component {
  userData;
  list;
  qnBuffer = []
  choiceBuffer = []

  constructor(props) {
      super(props);
      this.onChangeName = this.onChangeName.bind(this);
      this.onSubmit = this.onSubmit.bind(this);
      this.getOption = this.getOption.bind(this);
      this.generateDropdown = this.generateDropdown.bind(this);

      this.state = {
          name: '',
          colour: ''
      }
  }

  // Form Events
  onChangeName(e) {
    this.setState({ name: e.target.value })
  }

  onChangeColour(e) {
    //this.setState({ colour: e})
  }

  onSubmit(e) {
    e.preventDefault()
    this.setState({
        name: '',
        colour: ''
    })
  }

  // React Life Cycle
  componentDidMount() {
    this.userData = JSON.parse(localStorage.getItem('user'));
    if (localStorage.getItem('user')) {
        this.setState({
            name: this.userData.name,
            colour: this.userData.colour
        })
    } else {
        this.setState({
            name: '',
            colour: ''
        })
    }
}

componentWillUpdate(nextProps, nextState) {
    localStorage.setItem('user', JSON.stringify(nextState));
}

fetchData() {
  useEffect()
  fetch("http://localhost:3500/questions/list")
  .then(res => res.json())
  .then(resJson => {
    this.list = resJson.data
  })

  async function buffer() {
    var i = 0;
    try {
      await this.list.map(qn => {
        this.qnBuffer[i] = qn.question
        this.choiceBuffer[i] = this.cloneArr(qn.choices)
        i++;
      })
    } catch (error) {
      console.log("ERROR! List not ready")
    }
  }
  buffer()
}

getOption(_id) {
  var opt = document.getElementById(_id)
  return (opt.value)
}

cloneArr(arr) {
  let temp = []
  var i = 0;
  arr.map(item => {
    temp[i] = item
    i++
  })
  return (temp)
}

generateDropdown(arr) {
  return (
    arr.map(opt => <option>{opt}</option>)
  )
}

render () {
  return(
    <div>
      <form onSubmit={this.onSubmit}>
        <label>
          <p>{this.qnBuffer[0]}</p>
          <input
            type="text"
            value={this.state.name}
            onChange={this.onChangeName}
          />
        </label>

        <label>
          <p>{this.qnBuffer[1]}</p>
          <select id="qn2" onChange={() => this.onChangeColour(this.getOption("qn2"))}>
            {this.generateDropdown(this.choiceBuffer[1])}
          </select>
        </label>

        <p><input type="submit" /></p>
      </form>
    </div>
  )
  }
}

