import React from 'react'
import { useState, useEffect } from 'react'
import { useNavigate } from "react-router-dom";
import { Outlet, Link } from "react-router-dom";

function Survey() {
  // user's response
  const [name, setName] = useState("");
  const [colour, setColour] = useState("");

  // backend data 
  const [list, setList] = useState([]);

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(resJson => setList(resJson.data))
  }, [])

  let qnBuffer = [""]
  let choiceBuffer = [[], []]

  async function buffer() {
    var i = 0;
    try {
      await list.map(qn => {
        qnBuffer[i] = qn.question
        choiceBuffer[i] = cloneArr(qn.choices)
        i++;
      })
    } catch (error) {
      console.log("ERROR! List not ready")
    }
  }
  buffer()

  const navigate = useNavigate()
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(name)
    console.log(colour)
    navigate("/Summary")
    //alert(`Response submitted! Have a good day!`)
  }

  function getOption(_id) {
    var opt = document.getElementById(_id)
    console.log(opt.value)
    return (opt.value)
  }

  // return (
  //   <div>
  //     <form onSubmit={handleSubmit}>
  //       <label>
  //         <p>{qnBuffer[0]}</p>
  //           <input 
  //             type="text"
  //             value={name}
  //             onChange={(e) => setName(e.target.value)}
  //           />
  //       </label>

  //       <label>
  //         <p>{qnBuffer[1]}</p>
  //         <select id="qn2" onChange={() => setColour(getOption("qn2"))}>
  //           {generateDropdown(choiceBuffer[1])}
  //         </select>
  //       </label>

  //       <p><input type="submit" /></p>
  //     </form>
  //   </div>
  // )
    return (
      <>
        <nav>
          <ul>
            <li>
              <Link to="/Home">Home</Link>
            </li>
            <li>
              <Link to="/Summary">Blogs</Link>
            </li>
          </ul>
        </nav>

        <Outlet />
      </>
    )
}

function cloneArr(arr) {
  let temp = []
  var i = 0;
  arr.map(item => {
    temp[i] = item
    i++
  })
  return (temp)
}

function generateDropdown(arr) {
  return (
    arr.map(opt => <option>{opt}</option>)
  )
}

export default Survey;
