function Survey() {
  // user's response
  const [name, setName] = useState("");

  // backend data 
  const [qn, setQn] = useState({})
  const [qns, setQns] = useState([])
  const [list, setList] = useState(true)
  const [card, setCard] = useState(false)

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(resJson => setQns(resJson))
  }, []);

  useEffect(() => {
    fetch(`http://localhost:3500/questions/1`)
      .then(res => res.json())
      .then(resJson => {
        setQn(resJson.data);
        setList(false);
        setCard(true);
      });
  });

  let showList = () => {
    setCard(false)
    setList(true)
  }

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`The name you entered was: ${name}`)
  }

  return (
      <form onSubmit={handleSubmit}>
        <label>
          <p>{qn.question}</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <input type="submit" />
      </form>
