import { useState, useEffect } from 'react';

function Survey() {
  // user's response
  const [name, setName] = useState("");

  // backend data 
  const [qn, setQn] = useState({})
  const [qns, setQns] = useState([])
  // const [list, setList] = useState(true)
  // const [card, setCard] = useState(true)

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(res => setQns(res))
  }, []);

  // let showCard = () => {
  //   fetch(`http://localhost:3500/questions/1`)
  //     .then(res => res.json())
  //     .then(res => {
  //       setQn(res);
  //       setList(false);
  //       setCard(true);
  //     });
  // };

  // let showList = () => {
  //   setCard(false)
  //   setList(true)
  // }

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Response submitted! Have a good day ${name}!`)
  }

  //<p>{qns[0].question}</p>
  return (
      <form onSubmit={handleSubmit}>
        <label>
        <p>{qns[0].question}</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <input type="submit" />
      </form>
  )

}

export default Survey;
