const Home = require("./components/Home")
const Summary = require("./components/Summary")

function Survey() {
  // user's response
  const [name, setName] = useState("");

  // backend data 
  //const [qn, setQn] = useState({})
  const [qns, setQns] = useState([])
  const tempList = []
  // const [list, setList] = useState(true)
  // const [card, setCard] = useState(true)

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(res => {
      let temp = res;
      setQns(temp);
    })
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    //alert(`Response submitted! Have a good day!`)

  }

  return (
      <form onSubmit={handleSubmit}>
        <label>
          <p>{qns.data[0].question}</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>

        <input type="submit" />
      </form>
  )
}

export default Survey;


