import React from "react";
import { useState, useEffect } from 'react';
import {qnBuffer, choiceBuffer} from "./Home";
import Axios from 'axios';
import { useNavigate } from "react-router-dom";

function PostForm() {
  const url = "http//localhost:3500/questions/response/Create"
  const [data, setData] = useState({
    name: "",
    colour: "blue",
    languages: new Array(6).fill(false),
    iduser: ""
  })

  function handleChange(e) {
    const newData = { ...data } // copy curr data
    newData[e.target.id] = e.target.value
    // console.log(newData)
    // console.log(e.target.value)
    setData(newData)
    // console.log(data)
  }

  function handleSelected(e) {
    const sel = e.target.selectedOptions
    const temp = []
    for(let i = 0; i<sel.length; i++) {
      temp.push(sel[i].value)
    }
    const newData = { ...data } // copy curr data
    newData[e.target.id] = temp
    setData(newData)
  }

  const navigate = useNavigate()
  function onSubmit(e) {
    e.preventDefault();
    // Axios.post(url, {
    //   name: data.name,
    //   colour: data.colour
    // })
    // .then(res => {console.log(res.data)})
    // .then(navigate("/Summary"))
    console.log("submitted: ", data)
    //navigate("/Summary")
  }

  function generateDropdown(arr =[]) {
    return (
      arr.map(opt => <option>{opt}</option>)
    )
  }

  return (
    <div>
      <form>
        <div>
          <label>{qnBuffer[0]}</label>
          <p><input type="text" required value={data.name} onChange={handleChange} /></p>
        </div>
        <div>
          <label>{qnBuffer[1]}</label>
          <p><input type="checkbox"  required value={data.colour} onChange={handleChange} /> Blue</p>
          <p><input type="checkbox"  required value={data.colour} onChange={handleChange} /> Red</p>
          <p><input type="checkbox"  required value={data.colour} onChange={handleChange} /> Green</p>
          <p><input type="checkbox"  required value={data.colour} onChange={handleChange} /> Yellow</p>
          <p><input type="checkbox"  required value={data.colour} onChange={handleChange} /> Purple</p>
          <p><input type="checkbox"  required value={data.colour} onChange={handleChange} /> Pink</p>
        </div>




      </form>
    </div>
  )
}

export default PostForm

