import React from "react";
import { useState } from 'react';
import {qnBuffer, choiceBuffer} from "./Home";
import { useNavigate, Link } from "react-router-dom";
import {StyledButton, StyledForm, StyledFormWrapper} from "./Style.js"

function PostForm() {
  const resp_url = "/questions/responses" // to server 
  const navigate = useNavigate()
  const [data, setData] = useState({
    name: "",
    colour: {
      Blue: false,
      Red: false,
      Green: false,
      Yellow: false,
      Purple: false,
      Pink: false
    },
    prog_lang: {
      Python: false,
      Java: false,
      Ruby: false,
      JavaScript: false,
      Golang: false
    },
    languages: {
      English: false,
      Chinese: false,
      Malay: false,
      Tamil: false,
      Hindi: false
    },
    would_pay: {
      Yes: false,
      No: false
    }
  })

  function handleChange(e) {
    const newData = { ...data } // copy curr data
    newData[e.target.id] = e.target.value
    setData(newData)
  }

  function handleCheckbox(e) {
    const newData = { ...data } // copy curr data
    const updatedField = newData[e.target.id]
    updatedField[e.target.value] = e.target.checked
    newData[e.target.id] = updatedField
    setData(newData)
  }

  async function handleSubmit(e) {
    e.preventDefault();
    const newResponse = {
      method: "POST",
      mode: "cors",
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(data)
    }
    const response = await fetch(resp_url, newResponse) // post request to server 
    const server_response = await response.json()
    console.log("server replied:", server_response, server_response.id)
    const userId = server_response.id
    navigate(`/Summary/${userId}`)
  }

  function generateDropdown(arr =[]) {
    return (
      arr.map(opt => <option>{opt}</option>)
    )
  }

  async function generateCheckBoxes(arr =[], _id) {
    let field = await data[_id]
    console.log(field)
    return (
      arr.map(item => 
      <p><input type="checkbox" id={_id} value={item} onChange={handleCheckbox} checked={item}/> {item}</p>
      )
    )
  }

  return (
    <div>
      <StyledButton onClick={() => navigate(-1)}>Back</StyledButton>
      <StyledFormWrapper>
      <StyledForm onSubmit={e => handleSubmit(e)}>
        <div>
          <label>{qnBuffer[0]}</label>
          <p><input type="text" required id="name" value={data.name} onChange={handleChange} /></p>
        </div>
        <div>
          <label>{qnBuffer[1]}</label>
          <p>{generateCheckBoxes(choiceBuffer[1], data.colour)}</p>

        </div>
        <div>
          <label>{qnBuffer[2]}</label>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Python" onChange={handleCheckbox}/> Python</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Java" onChange={handleCheckbox}/> Java</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Ruby" onChange={handleCheckbox}/> Ruby</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="JavaScript" onChange={handleCheckbox}/> JavaScript</p>
          <p><input type="radio" name="prog_lang" id="prog_lang" value="Golang" onChange={handleCheckbox}/> Golang</p>
        </div>
        <div>
          <label>{qnBuffer[3]}</label>
          <p><input type="checkbox" id="languages" value="English" onChange={handleCheckbox} checked={data.languages.English}/> English</p>
          <p><input type="checkbox" id="languages" value="Chinese" onChange={handleCheckbox} checked={data.languages.Chinese}/> Chinese</p>
          <p><input type="checkbox" id="languages" value="Malay" onChange={handleCheckbox} checked={data.languages.Malay}/> Malay</p>
          <p><input type="checkbox" id="languages" value="Tamil" onChange={handleCheckbox} checked={data.languages.Tamil}/> Tamil</p>
          <p><input type="checkbox" id="languages" value="Hindi" onChange={handleCheckbox} checked={data.languages.Hindi}/> Hindi</p>
        </div>
        <div>
          <label>{qnBuffer[4]}</label>
          <p><input type="radio" name="would_pay" id="would_pay" value="Yes" onChange={handleCheckbox}/> Yes</p>
          <p><input type="radio" name="would_pay" id="would_pay" value="No" onChange={handleCheckbox}/> No</p>
        </div>
        <StyledButton type="submit">submit</StyledButton>
      </StyledForm>
      </StyledFormWrapper>
    </div>
  )
}

export default PostForm

