import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { StyledDiv, StyledWrapper, StyledHeader } from "./Style.jsx"

function ResponseDisplay() {

  const navigate = useNavigate()
  const [list, setList] = useState([])
  const clear_url = "/questions/responses"

  async function handleEdit(index) {
    navigate(`/Edit/${index}`) // navigates to summary page 
  }

  async function handleClear(e) {
    const newResponse = {
      method: "PUT",
      mode: "cors",
    }
    const response = await fetch(clear_url, newResponse) // put request to server 
    const server_response = await response.json()
    console.log("server replied:", server_response)
    navigate(0)
    alert("Responses cleared")
  }

  // fetch data
  useEffect(() => {
    fetch("http://localhost:3500/questions/responses")
      .then(res => res.json())
      .then(resJson => {
        _setList(resJson)
      })
  }, [])

  const [data, setData] = useState({
    name: " ",
    colour: {
      Blue: false,
      Red: false,
      Green: false,
      Yellow: false,
      Purple: false,
      Pink: false
    },
    prog_lang: {
      Python: false,
      Java: false,
      Ruby: false,
      JavaScript: false,
      Golang: false
    },
    languages: {
      English: false,
      Chinese: false,
      Malay: false,
      Tamil: false,
      Hindi: false
    },
    would_pay: {
      Yes: false,
      No: false
    }
  })

  async function _setList(data = []) {
    let temp = []
    let len = data.length 
    for(let i = 0; i < len; i++) {
      let curr = data[i]
      for(const key in curr) {
        if(key === "name" || key === "id") {
          data[key] = curr[key]
        } else {
          data[key] = parseString(curr[key], key)
        }
      }
      temp[i] = {...data}
      //console.log("TEMP", temp[i])
    }
    console.log("temp", temp)
