import React from "react";
import { useState, useEffect } from 'react';
import { qnBuffer } from "./Survey";
import { useNavigate } from "react-router-dom";
import { StyledButton, StyledForm, StyledFormWrapper } from "./Style.jsx"

function PostForm() {
  const resp_url = "/questions/responses" // to server 
  const navigate = useNavigate()
  const [questions, setQuestions] = useState([])
  const [choices, setChoices] = useState({
    1: {},
    2: {},
    3: {},
    4: {},
    5: {}
  })
  const [data, setData] = useState({
    name: " ",
    colour: {
      Blue: false,
      Red: false,
      Green: false,
      Yellow: false,
      Purple: false,
      Pink: false
    },
    prog_lang: {
      Python: false,
      Java: false,
      Ruby: false,
      JavaScript: false,
      Golang: false
    },
    languages: {
      English: false,
      Chinese: false,
      Malay: false,
      Tamil: false,
      Hindi: false
    },
    would_pay: {
      Yes: false,
      No: false
    }
  })

  function getField(questionId) {
    switch (questionId) {
      case 1:
        return "name"
      case 2:
        return "colour"
      case 3:
        return "prog_lang"
      case 4:
        return "langugaes"
      case 5:
        return "would_pay"
      default:
        return "invalid id"
    }
  }

  // fecth questions from server 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
      .then(res => res.json())
      .then(resJson => setQuestions(resJson))
  }, [questions])

  // fecth choices from server
  useEffect(() => {
    const len = questions.length
    for(let i = 1; i <= len; i++) {
      fetch(`http://localhost:3500/questions/choice/${i}`)
      .then(res => res.json())
      .then(resJson => {
        const newData = { ...choices }
        newData[i] = resJson
        setChoices(newData)
      }).then(console.log(choices))
    }
  }, [])

  function handleChange(e) {
    const newData = { ...data } // copy curr data
    newData[e.target.id] = e.target.value
    setData(newData)
  }

  function handleCheckbox(e) {
    const newData = { ...data } // copy curr data
    const updatedField = newData[e.target.id]
    updatedField[e.target.value] = e.target.checked
    newData[e.target.id] = updatedField
    setData(newData)
  }

  async function handleSubmit(e) {
    e.preventDefault();
    const newResponse = {
      method: "POST",
      mode: "cors",
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(data)
    }
    //console.log("client sent: ", newResponse.body)
    const response = await fetch(resp_url, newResponse) // post request to server 
    const server_response = await response.json()
    console.log("server replied: ", server_response)
    navigate("/Result")
  }

  /**
   * Generates HTML script to produce checkbox or radio inputs
   */
  function generateCheckBoxes(arr = [], _id, _type) {
    let field = data[_id]
    return (
      arr.map(item =>
        <p><input type={_type} name={_id} id={_id} value={item} onChange={handleCheckbox} checked={field.item} /> {item}</p>
      )
    )
  }

  /**
   * Generates HTML script to produce inputs
   */
  function generateInput(questionId, _type) {
    let field = getField(questionId) // value
    const options = choices[questionId]

    switch (_type) {
      case "textbox":
        return (<input type="text" required id={field} value={data.field} onChange={handleChange} />)
      case "radio":
        return generateCheckBoxes(options, field, "radio")
      case "checkbox":
        return generateCheckBoxes(options, field, "checkbox")
      default:
        return (<div>Loading...</div>)
    }
  }

  let iterator = []; // stores form's script
  questions.forEach((item, index) => {
    const keys = Object.keys(item)
    iterator.push(<div><label>{item[keys[1]]}</label></div>) // question
    iterator.push(<div>{generateInput(item[keys[0]], item[keys[2]])}</div>) // choices
    console.log(choices)
  })

  return (
    <div>
      <StyledButton onClick={() => navigate(-1)}>Back</StyledButton>
      <StyledFormWrapper>
        <StyledForm onSubmit={e => handleSubmit(e)}>
          <div>
            <label>{iterator}</label>
          </div>
          <StyledButton type="submit">submit</StyledButton>
        </StyledForm>
      </StyledFormWrapper>
    </div>
  )
}

export default PostForm
