import React from "react";
import { useState, useEffect } from 'react';
import {qnBuffer, choiceBuffer} from "./Home";
import Axios from 'axios';

function PostForm() {
  const url = ""
  const [data, setData] = useState({
    name: "",
    colour: "",
    iduser: ""
  })

  function handleChange(e) {
    const newData = { ...data }
    newData[e.target.id] = e.target.value
  }

  function onSubmit(e) {
    e.preventDefault();
    Axios.post(url, {
      name: data.name,
      colour: data.colour
    })
    .then(res => {console.log(res.data)})
  }

  function generateDropdown(arr =[]) {
    return (
      arr.map(opt => <option>{opt}</option>)
    )
  }

  return (
    <div className='container'>
      <form onSubmit={e => onSubmit(e)}>
        <label>
          <p>{qnBuffer[0]}</p>
          <input
            type="text" required
            id="name"
            value={data.name}
            onChange={(e) => handleChange(e)}
          />
        </label>

        <label>
          <p>{qnBuffer[1]}</p>
          <select 
            id="colour"
            value={data.colour}
            onChange={(e) => handleChange(e)}
            >
            {generateDropdown(choiceBuffer[1])}
          </select>
        </label>

        <p><input type="submit" /></p>
      </form>
    </div>
  )
}

export default PostForm
