import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { StyledDiv, StyledWrapper, StyledHeader } from "./Style.jsx"

function ResponseDisplay() {

  const navigate = useNavigate()
  const [list, setList] = useState([])

  async function handleEdit(index) {
    navigate(`/Edit/${index}`) // navigates to summary page 
  }

  // fetch data
  useEffect(() => {
    fetch("http://localhost:3500/questions/responses")
      .then(res => res.json())
      .then(resJson => {
        _setList(resJson)
        console.log("display fetched: ", resJson)
      })
  }, [])

  const [data, setData] = useState({
    name: " ",
    colour: {
      Blue: false,
      Red: false,
      Green: false,
      Yellow: false,
      Purple: false,
      Pink: false
    },
    prog_lang: {
      Python: false,
      Java: false,
      Ruby: false,
      JavaScript: false,
      Golang: false
    },
    languages: {
      English: false,
      Chinese: false,
      Malay: false,
      Tamil: false,
      Hindi: false
    },
    would_pay: {
      Yes: false,
      No: false
    }
  })

  function _setList(data = []) {
    let temp = []
    let len = data.length
    for(let i = 0; i < len; i++) {
      let curr = data[i]
      //let keys = Objects.keys(curr)
      for(const key in curr) {
        if(key === "name" || key === "id") {
          setData({key: curr[key]})
        } else {
          setData({key: parseString(curr[key], key)})
        }
      }
      temp[i] = {...data}
    }
    setList(te)
  }

    // "{\"Blue\":true,\"Red\":true,\"Green\":false,\"Yellow\":false,\"Purple\":false,\"Pink\":false"
  function parseString(str, field) {
    //let arr = str.split("\\")
    let temp = {...data[field]}
    for(const key in data[field]) {
      let len = key.length
      let pos = str.search(key)
      let end_pos = pos === -1 ? 0 : pos + len + 3
      if(str.charAt(end_pos) == "t") {
        temp[key] = true
      } else {
        temp[key] = false
      }
    }
    return temp
  }

  function displayBox(person) {
    let content = [];
    const keys = Object.keys(person)
    const len = keys.length
    for (let i = 0; i < len; i++) {
      if (keys[i] === "name" || keys[i] === "id") {
        content.push(<div>{keys[i]} :{person[keys[i]]}</div>)
      } else {
        let selected = displaySelected(person[keys[i]])
        content.push(<p>{keys[i]}: {selected}</p>)
      }
    }
    return content
  }

  // function displaySelected(field) {
  //   const keys = Object.keys(field)
  //   console.log(typeof(field))
  //   //console.log("displaySelected:", JSON.parse(field))
  //   const len = keys.length
  //   let res = ""
  //   for (let i = 0; i < len; i++) {
  //     if (field[keys[i]]) {
  //       res += keys[i] + ", "
  //     } else {
  //       // continue
  //     }
  //   }
  //   return res.length === 0 ? "None" : res
  // }

  function displaySelected(person) {
    console.log(person) 
  }

  function displayResult(items = []) {
    items.forEach((result, index) => {
      responses.push(<StyledDiv onClick={() => { handleEdit(index) }}>
        <p>{displayBox(result)}</p>
      </StyledDiv>)
    })
  }

  let responses = []
  displayResult(list)

  return (
    <div>
      <button onClick={() => { navigate(-1) }}>Back</button>
      <StyledHeader>Summary</StyledHeader>
      <StyledWrapper>
        <label>You may click on the response to view or edit</label>
        <div>{responses}</div>
      </StyledWrapper>
    </div>
  )
}

export default ResponseDisplay
