import React from "react";
import { useState } from 'react';
import {qnBuffer, choiceBuffer} from "./Home";
import Axios from 'axios';
import { useNavigate } from "react-router-dom";

function PostForm() {
  const url = "http//localhost:3500/questions/response/Create"
  const [data, setData] = useState({
    name: "",
    colour: {
      blue: false,
      red: false,
      green: false,
      yellow: false,
      purple: false,
      pink: false
    }
  })

  function handleChange(e) {
    const newData = { ...data } // copy curr data
    newData[e.target.id] = e.target.value
    setData(newData)
  }

  function handleCheckbox(e) {
    let val = e.target.value
    let field = e.target.id
    let req = data[field]
    req[val] = e.target.checked
    setData({})
  }

  const navigate = useNavigate()
  function handleSubmit(e) {
    e.preventDefault();
    // Axios.post(url, {
    //   name: data.name,
    //   colour: data.colour
    // })
    // .then(res => {console.log(res.data)})
    // .then(navigate("/Summary"))
    console.log("submitted: ", data)
    //navigate("/Summary")
  }

  function generateDropdown(arr =[]) {
    return (
      arr.map(opt => <option>{opt}</option>)
    )
  }

  return (
    <div>
      <form onSubmit={e => handleSubmit(e)}>
        <div>
          <label>{qnBuffer[0]}</label>
          <p><input type="text" required id="name" value={data.name} onChange={e => handleChange(e)} /></p>
        </div>
        <div>
          <label>{qnBuffer[1]}</label>
          <p><input type="checkbox" id="colour" value="Blue" onChange={handleCheckbox} checked={data.colour.blue}/> Blue</p>
          <p><input type="checkbox" id="colour" value="Red" onChange={handleCheckbox} checked={data.colour.red}/> Red</p>
          <p><input type="checkbox" id="colour" value="Green" onChange={handleCheckbox} checked={data.colour.green}/> Green</p>
          <p><input type="checkbox" id="colour" value="Yellow" onChange={handleCheckbox} checked={data.colour.yellow}/> Yellow</p>
          <p><input type="checkbox" id="colour" value="purple" onChange={handleCheckbox} checked={data.colour.purple}/> Purple</p>
          <p><input type="checkbox" id="colour" value="Pink" onChange={handleCheckbox} checked={data.colour.pink}/> Pink</p>
        </div>
        <button type="submit">submit</button>
      </form>
    </div>
  )
}

export default PostForm

