import React from 'react'
import { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect,} from "react-router-dom";
import ReactDOM from 'react-dom'

function Survey() {
  // user's response
  const [name, setName] = useState("");
  const [colour, setColour] = useState("");

  // backend data 
  const [list, setList] = useState([]);

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(resJson => setList(resJson.data))
  }, [])


  let dataBuffer = []
  async function buffer() {
    var i = 0;
    try {
      await list.map(qn => {
        dataBuffer[i] = qn.question
        i++;
      })
    } catch (error) {
      console.log("ERROR! List not ready")
    }
  }
  buffer()

  const handleSubmit = (e) => {
    e.preventDefault();
    setName(e.target.value)
    alert(`Response submitted! Have a good day!`)
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>

        <label>
          <p>{dataBuffer[0]}</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>

        <label>
        <p>{dataBuffer[1]}</p>
        <select></select>

        </label>

        <p><input type="submit" /></p>
      </form>
    </div>
  )
}
// {list.map(qn => <p>{qn.question}</p>)}
export default Survey;
