import { useState, useEffect } from 'react';

function GetQuestion() {
  const [data, setData] = useState({});

  useEffect(() => {
    fetch("/")
    .then(res => res.json())
    .then(dataJson => setData(dataJson.question))
  }, [])

  return (
    <><div>{data.question}</div><div>{data.questionType}</div><div>{data.choices}</div></>
  )
}

function Survey() {
  const [card, setCard] = useState(true); 
  const [list, setList] = useState(true); // list of questions will be displayed 
  const [name, setName] = useState("");
  const [data, setData] = useState([]); // question (temp)

  useEffect(() => {
    fetch("/")
    .then(res => res.json())
    .then(dataJson => setData(dataJson.question))
  }, []);


  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`The name you entered was: ${name}`)
  }

  return (
    <div classNmae="container">
    <form onSubmit={handleSubmit}>
      <label>

        <input 
          type="text"
          value={name}
          onChange={(e) => setName(e.target.value)}
        />
      </label>
      <input type="submit" />
    </form>
    /div></div>
  )
}

export default Survey;
