import React from "react";
import { useState, useEffect } from 'react';
import {qnBuffer, choiceBuffer} from "./Home";
import Axios from 'axios';
import { useNavigate } from "react-router-dom";

function PostForm() {
  const url = "http//localhost:3500/questions/response/Create"
  const [data, setData] = useState({
    name: "",
    colour: "blue",
    languages: [],
    iduser: ""
  })

  function handleChange(e) {
    const newData = { ...data } // copy curr data
    newData[e.target.id] = e.target.value
    // console.log(newData)
    // console.log(e.target.value)
    setData(newData)
    // console.log(data)
  }

  function handleSelected(e) {
    const sel = e.target.selectedOptions
    const temp = []
    for(let i = 0; i<sel.length; i++) {
      temp.push(sel[i].value)
    }
    const newData = { ...data } // copy curr data
    newData[e.target.id] = temp
    setData(newData)
  }

  const navigate = useNavigate()
  function onSubmit(e) {
    e.preventDefault();
    // Axios.post(url, {
    //   name: data.name,
    //   colour: data.colour
    // })
    // .then(res => {console.log(res.data)})
    // .then(navigate("/Summary"))
    console.log(data.name + data.colour)
    navigate("/Summary")
  }

  function generateDropdown(arr =[]) {
    return (
      arr.map(opt => <option>{opt}</option>)
    )
  }

  return (
    <div className='container'>
      <form onSubmit={e => onSubmit(e)}>
        <label>
          <p>{qnBuffer[0]}</p>
          <input
            type="text" required
            id="name"
            value={data.name}
            onChange={(e) => handleChange(e)}
          />
        </label>

        <label>
          <p>{qnBuffer[1]}</p>
          <select 
            id="colour"
            value={data.colour}
            onChange={(e) => handleChange(e)}
            >
            {generateDropdown(choiceBuffer[1])}
          </select>
        </label>

        <label>
          <p>{qnBuffer[2]}</p>
          <input type="radio" id="languages" value={data.languages} onChange={(e) => handleChange(e)}/>Python
          <input type="radio" id="languages" value={data.languages} onChange={(e) => handleChange(e)}/>Java

        </label>



        <p><input type="submit" /></p>
      </form>
    </div>
  )
}

export default PostForm
