import React, { useState, useEffect } from "react";
import { renderMatches, useNavigate, useParams } from "react-router-dom";
import { StyledWrapper, StyledHeader } from "./Style.jsx"
import ViewForm from "./ViewForm.jsx";

function ResponseDisplay() {

  let { pos } = useParams() // indexed at 0
  let _pos = Number(pos)
  const [ids, setIds] = useState([]) 
  const navigate = useNavigate()

  useEffect(() => {
    const fetchData = async () => {
      const _data = await fetch("http://localhost:3500/questions/responses/ids")
        .then(res => res.json())
        .then(resJson => setIds(resJson.data))
    }

    fetchData()
      .catch(console.error)
  }, [])

  console.log(ids)
  const curr_id = ids[_pos]
  console.log(pos)
  console.log("curr", curr_id)
  const max = ids.length 
  let prev_invalid = _pos > 0 ? false : true
  let next_invalid = _pos < max - 1 ? false : true

  return (
    <div>
      <button onClick={() => { navigate("/") }}>Home</button>
      <div align="center">
        <StyledHeader>Response {_pos}/{max}</StyledHeader>
        <button disabled={prev_invalid} onClick={() => { navigate(`/Result/${_pos - 1}`) }}>Prev</button>
        <button disabled={next_invalid} onClick={() => { navigate(`/Result/${_pos + 1}`) }}>Next</button>
      </div>
      {ViewForm(curr_id)}
    </div>
  )
}

export default ResponseDisplay
