import React from 'react'
import { useState, useEffect } from 'react'
import { BrowserRouter as Router, Switch, Route, Redirect,} from "react-router-dom";
import ReactDOM from 'react-dom'

function Survey() {
  // user's response
  const [name, setName] = useState("");

  // backend data 
  const [list, setList] = useState([]);

  // fetch list of questions 
  useEffect(() => {
    fetch("http://localhost:3500/questions/list")
    .then(res => res.json())
    .then(resJson => setList(resJson.data))
  }, [])

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(list[0].question)
    alert(`Response submitted! Have a good day!`)
  }

  function buffer() {
    console.log(list[0]._id)
    console.log(list[0]._question)
  }

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          <p>{typeof(list[0])}</p>
          <input 
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
        </label>
        <input type="submit" />
      </form>
    </div>
  )
}

export default Survey;

