import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { StyledDiv, StyledWrapper, StyledHeader } from "./Style.jsx"
import PostForm from "./PostForm.jsx";

function ResponseDisplay() {

  const navigate = useNavigate()
  const [responses, setResponse] = useState([])
  const clear_url = "/questions/responses"

  async function handleEdit(index) {
    navigate(`/Edit/${index}`) // navigates to summary page 
  }

  async function handleClear(e) {
    const newResponse = {
      method: "PUT",
      mode: "cors",
    }
    const response = await fetch(clear_url, newResponse) // put request to server 
    const server_response = await response.json()
    console.log("server replied:", server_response)
    navigate(0)
    alert("Responses cleared")
  }

  // fetch data
  useEffect(() => {
    const fetchData = async () => {
      const data = await fetch("http://localhost:3500/questions/responses")
      .then(res => res.json())
      .then(resJson => {
        console.log(resJson)
        })
    }

    fetchData()
    .then(() => {setResponse(data)
      console.log(responses)})
    .catch(console.error)
  } , [])

  function displayBox(person) {
    let content = [];
    const keys = Object.keys(person)
    const len = keys.length
    for (let i = 0; i < len; i++) {
      if (keys[i] === "name" || keys[i] === "id") {
        content.push(<div>{keys[i]} :{person[keys[i]]}</div>)
      } else {
        let selected = displaySelected(person[keys[i]])
        content.push(<p>{keys[i]}: {selected}</p>)
      }
    }
    console.log("CONTENT", content)
    return content
  }

  function displaySelected(field) {
    const keys = Object.keys(field)
    const len = keys.length
    let res = ""
    for (let i = 0; i < len; i++) {
      if (field[keys[i]]) {
        res += keys[i] + ", "
      } else {
        // continue
      }
    }
    return res.length === 0 ? "None" : res
  }

  async function displayResult(items = []) {
    console.log(items)
    Array.prototype.forEach.call(items, (result) => {
      console.log(result)
      let index = index
      responses.push(<StyledDiv onClick={() => { handleEdit(index) }}>
        <p>{displayBox(result)}</p>
      </StyledDiv>)
    })
  }


   const forms = []

  // const list_keys = Object.keys(responses)
  // for(var key in list_keys) {
  //   forms.push(makeForm(responses[key]))
  // }

  return (
    <div>
      <button onClick={() => { navigate(-1) }}>Back</button>
      <button onClick={(e) => { handleClear(e) }}>Clear</button>
      <StyledHeader>Summary</StyledHeader>
      <StyledWrapper>
        <label>You may click on the response to view or edit</label>
        <div>{forms}</div>
      </StyledWrapper>
    </div>
  )
}

export default ResponseDisplay
